const input = $('Edit Fields1').first().json.conversation?.trim();

function isCPF(value) {
  return /^\d{3}\.?\d{3}\.?\d{3}-?\d{2}$/.test(value);
}

function isCNPJ(value) {
  return /^\d{2}\.?\d{3}\.?\d{3}\/?\d{4}-?\d{2}$/.test(value);
}

function isDate(value) {
  return /^\d{2}\/\d{2}\/\d{4}$/.test(value) || /^\d{4}-\d{2}-\d{2}$/.test(value);
}

function formatDate(value) {
  // Se for dd/MM/yyyy, converte para yyyy-MM-dd
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(value)) {
    const [day, month, year] = value.split('/');
    return `${year}-${month}-${day}`;
  }
  // Se j√° estiver no formato yyyy-MM-dd, retorna como est√°
  return value;
}

let tipo = "Desconhecido";
let valorTratado = input;

if (isCPF(input)) {
  tipo = "CPF";
} else if (isCNPJ(input)) {
  tipo = "CNPJ";
} else if (isDate(input)) {
  tipo = "Data";
  valorTratado = formatDate(input);
}

return [
  {
    json: {
      tipoCampo: tipo,
      valor: valorTratado,
    }
  }
];
